import React, { useState } from 'react';

const mockProducts = [
  { id: 1, name: "Paracetamol 500mg", price: 15.00, stock: 120 },
  { id: 2, name: "Amoxicillin 250mg", price: 30.00, stock: 80 },
  { id: 3, name: "Vitamin C 100mg", price: 12.00, stock: 200 },
];

const Billing = () => {
  const [search, setSearch] = useState('');
  const [cart, setCart] = useState([]);
  const [customer, setCustomer] = useState('Walk-in');
  const products = mockProducts.filter(p => p.name.toLowerCase().includes(search.toLowerCase()));

  const addToCart = (product) => {
    setCart(prev => {
      const found = prev.find(item => item.id === product.id);
      if (found) {
        return prev.map(item =>
          item.id === product.id
            ? { ...item, qty: item.qty + 1 }
            : item
        );
      }
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart(prev => prev.filter(item => item.id !== id));
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  return (
    <div>
      <h2>Sales / Billing</h2>
      <div style={{ marginBottom: 20 }}>
        <label>
          Customer:&nbsp;
          <select value={customer} onChange={e => setCustomer(e.target.value)}>
            <option>Walk-in</option>
            <option>John Doe</option>
            <option>Jane Smith</option>
          </select>
        </label>
      </div>
      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Search product..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </div>
      <table style={{ width: '100%', marginBottom: 20 }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Add</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.name}</td>
              <td>Rs. {p.price.toFixed(2)}</td>
              <td>{p.stock}</td>
              <td>
                <button onClick={() => addToCart(p)}>Add</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Cart</h3>
      <table style={{ width: '100%', marginBottom: 20 }}>
        <thead>
          <tr>
            <th>Product</th>
            <th>Qty</th>
            <th>Unit Price</th>
            <th>Subtotal</th>
            <th>Remove</th>
          </tr>
        </thead>
        <tbody>
          {cart.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.qty}</td>
              <td>Rs. {item.price.toFixed(2)}</td>
              <td>Rs. {(item.price * item.qty).toFixed(2)}</td>
              <td><button onClick={() => removeFromCart(item.id)}>Remove</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <div style={{ fontWeight: 'bold', fontSize: 18 }}>
        Total: Rs. {total.toFixed(2)}
      </div>
      <button style={{ marginTop: 20 }}>Process Payment</button>
    </div>
  );
};

export default Billing;
