import React, { useState } from 'react';

const suppliers = [
  { id: 1, name: 'HealthCorp' },
  { id: 2, name: 'MediSupply' },
];

const products = [
  { id: 1, name: 'Paracetamol' },
  { id: 2, name: 'Ibuprofen' },
];

const PurchaseOrder = () => {
  const [supplier, setSupplier] = useState('');
  const [date, setDate] = useState('');
  const [poNumber, setPoNumber] = useState('');
  const [orderNumber, setOrderNumber] = useState('');
  const [product, setProduct] = useState('');
  const [costPrice, setCostPrice] = useState('');
  const [sellPrice, setSellPrice] = useState('');
  const [stock, setStock] = useState('');
  const [barcode, setBarcode] = useState('');
  const [discount, setDiscount] = useState('');
  const [discountAmount, setDiscountAmount] = useState('');
  const [productList, setProductList] = useState([]);

  const handleAddProduct = () => {
    setProductList([
      ...productList,
      {
        id: Date.now(),
        product,
        costPrice,
        sellPrice,
        stock,
        barcode,
        discount,
        discountAmount,
      },
    ]);
    setProduct('');
    setCostPrice('');
    setSellPrice('');
    setStock('');
    setBarcode('');
    setDiscount('');
    setDiscountAmount('');
  };

  const handleDeleteProduct = (id) => {
    setProductList(productList.filter((p) => p.id !== id));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save purchase order logic here
    alert('Purchase Order Submitted!');
  };

  return (
    <div style={{ padding: 24 }}>
      <h2>Create Purchase Order</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Supplier: </label>
          <select value={supplier} onChange={e => setSupplier(e.target.value)} required>
            <option value="">Select Supplier</option>
            {suppliers.map(s => (
              <option key={s.id} value={s.id}>{s.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Date: </label>
          <input type="date" value={date} onChange={e => setDate(e.target.value)} required />
        </div>
        <div>
          <label>PO Number: </label>
          <input type="text" value={poNumber} onChange={e => setPoNumber(e.target.value)} placeholder="Auto-generated or enter manually" />
        </div>
        <div>
          <label>Order Number: </label>
          <input type="text" value={orderNumber} onChange={e => setOrderNumber(e.target.value)} />
        </div>
        <hr />
        <h3>Add Products</h3>
        <div>
          <label>Product: </label>
          <select value={product} onChange={e => setProduct(e.target.value)}>
            <option value="">Select Product</option>
            {products.map(p => (
              <option key={p.id} value={p.name}>{p.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Cost Price: </label>
          <input type="number" value={costPrice} onChange={e => setCostPrice(e.target.value)} />
        </div>
        <div>
          <label>Sell Price: </label>
          <input type="number" value={sellPrice} onChange={e => setSellPrice(e.target.value)} />
        </div>
        <div>
          <label>Stock: </label>
          <input type="number" value={stock} onChange={e => setStock(e.target.value)} />
        </div>
        <div>
          <label>Barcode: </label>
          <input type="text" value={barcode} onChange={e => setBarcode(e.target.value)} />
        </div>
        <div>
          <label>Discount (%): </label>
          <input type="number" value={discount} onChange={e => setDiscount(e.target.value)} />
        </div>
        <div>
          <label>Discount Amount: </label>
          <input type="number" value={discountAmount} onChange={e => setDiscountAmount(e.target.value)} />
        </div>
        <button type="button" onClick={handleAddProduct} style={{ marginTop: 8 }}>Add Product</button>
        <hr />
        <h3>Products Added</h3>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ background: '#f0f0f0' }}>
              <th>Product</th>
              <th>Cost Price</th>
              <th>Sell Price</th>
              <th>Stock</th>
              <th>Barcode</th>
              <th>Discount (%)</th>
              <th>Discount Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {productList.map(p => (
              <tr key={p.id}>
                <td>{p.product}</td>
                <td>{p.costPrice}</td>
                <td>{p.sellPrice}</td>
                <td>{p.stock}</td>
                <td>{p.barcode}</td>
                <td>{p.discount}</td>
                <td>{p.discountAmount}</td>
                <td>
                  {/* Edit functionality can be added here */}
                  <button onClick={() => handleDeleteProduct(p.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button type="submit" style={{ marginTop: 16 }}>Submit Purchase Order</button>
      </form>
    </div>
  );
};

export default PurchaseOrder;
